import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Category } from 'src/app/common/Category';
import { Product } from 'src/app/common/Product';
import { ProductService } from 'src/app/service/product.service';
import swal from 'sweetalert2';
@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent implements OnInit {
product:Product =  new Product();

categories :Category[]=[];
  constructor(
private productService:ProductService,
private router:Router

  ) { }

  ngOnInit() {
    this.listProductCategories();
  }

  listProductCategories() {
    this.productService.getProductCategories().subscribe(
      data => {
        console.log('Product Categories=' + JSON.stringify(data));
        this.categories = data;
      }
    );
  }
  onSubmit(type: number) {
    this.productService.postProduct(this.product, type).subscribe(
      (data) => {
        console.log(data);
        swal.fire('Success', 'Product Successfully added!', 'success');
        this.product = new Product();
        this.goToList();
      },
      (error) => {
        console.log(error);
        swal.fire('Error', 'Cannot post property. Try again!', 'error');
      }
    );
  }
  goToList() {
    this.router.navigate(['/home']);
  }

}
<div class="container-fluid">
  <div class="row mt-3">
    <div class="col-lg-4"></div>
    <div class="col-lg-4">
      <form class="form-container">
        <div class="card mt-3 bg-white">
          <h4 class="text-left ml-4">
            Add new Product <span class="fa fa-1x fa-pencil-square"></span>
          </h4>
          <div class="card-body">
            <form (ngSubmit)="onSubmit(+type.value)">
              <div class="form-group">
                <label>Type</label>
                <select class="form-control" #type>
                  <option
                    *ngFor="let category of categories"
                    value="{{ category.id }}"
                  >
                    {{ category.type }}
                  </option>
                </select>
              </div>

              <div class="form-group">
                <input
                  placeholder="name"
                  type="text"
                  id="name"
                  [(ngModel)]="product.name"
                  name="name"
                  class="form-control"
                  required
                />
              </div>
              <div class="form-group">
                <input
                  placeholder="description"
                  type="text"
                  id="description"
                  [(ngModel)]="product.description"
                  name="description"
                  class="form-control"
                  required
                />
              </div>
              <div class="form-group">
                <input
                  placeholder="price"
                  type="text"
                  id="price"
                  [(ngModel)]="product.price"
                  name="price"
                  class="form-control"
                  required
                />
              </div>
              <div class="form-group">
                <input
                  placeholder="Image url"
                  type="text"
                  id="imageUrl"
                  [(ngModel)]="product.imageUrl"
                  name="imageUrl"
                  class="form-control"
                  required
                />
              </div>

              <div class="form-group">
                <button type="submit" class="btn btn-success btn-sm">
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </form>
    </div>
    <div class="col-lg-1"></div>
  </div>
</div>
