import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthenticationService } from 'src/app/service/authentication.service';
import swal from 'sweetalert2';

@Component({
  selector: 'app-Login',
  templateUrl: './Login.component.html',
  styleUrls: ['./Login.component.css']
})
export class LoginComponent implements OnInit {

  form: FormGroup;

  username = '';
  password = '';
  @Input() error: string | null;
  invalidLogin = false;

  constructor(
    public router: Router,
    public loginservice: AuthenticationService,
    private formBuilder: FormBuilder
  ) {}

  ngOnInit(): void {
    this.form = this.formBuilder.group({
      username: [null, [Validators.required, Validators.email]],
      password: [null, [Validators.required, Validators.minLength(6)]],
    });
  }

  checkLogin() {
    this.loginservice.authenticate(this.username, this.password).subscribe(
      (data) => {
       this.invalidLogin = false;
          this.router.navigate(['home']);

        // else {
        //   this.router.navigate(['admindashboard']);
        //   this.invalidLogin =false;
        // }

      },
      (error) => {
        this.invalidLogin = true;
        swal.fire('Error', 'Please check your credentials', 'error');
      }
    );
  }
  reloadPage(): void {
    this.router.navigate(['home']);
  }
}
<div class="modal-body">
  <div class="container-fluid">
    <div class="row">
      <div class="col-lg-4 m-auto">
        <form class="form-container">
          <div class="card mt-2 bg-white">
            <div class="card-title text-center mt-3">
              <img
                src="../../../assets/images/AvtarNew.png"
                height="140px"
                width="140px"
                class="img-rounded"
              />
            </div>
            <div class="card-body">
              <form (ngSubmit)="checkLogin()" [formGroup]="form">
                <div class="input-group mb-3">
                  <div class="input-group-prepend">
                    <span class="input-group-text">
                      <i class="fa fa-user fa-1x"></i>
                    </span>
                  </div>
                  <input
                    type="email"
                    class="form-control py-2"
                    id="exampleInputEmail1"
                    placeholder="Enter your email address"
                    [(ngModel)]="username"
                    required
                    formControlName="username"
                  />
                </div>
                <div class="input-group mb-3">
                  <div class="input-group-prepend">
                    <span class="input-group-text">
                      <i class="fa fa-lock fa-1x"></i>
                    </span>
                  </div>
                  <input
                    type="password"
                    class="form-control py-2"
                    id="exampleInputPassword"
                    placeholder="Enter your password"
                    [(ngModel)]="password"
                    required
                    formControlName="password"
                  />
                </div>

                <button
                  [disabled]="!form.valid"
                  type="submit"
                  class="btn btn-block"
                  style="background-color: #00ffbf; color: rgb(79, 77, 77)"
                >
                  Log in</button
                ><br />

                <p class="float-right mt-2">
                  New User ?<a routerLink="/register" style="color: #6bdb74">
                    Create Account</a
                  >
                </p>
                <br />
              </form>
            </div>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
